def selection_sort(arr):
  n=len(arr)
  for i in range(n):
    min=i
    for j in range(i+1,n):
      if arr[j]<arr[min]:
        min=j
    arr[i],arr[min]=arr[min],arr[i]

  return arr

def bubble_sort(arr):
  n=len(arr)
  for i in range(n):
    swapped= False
    for j in range(i,n-i-1):
      if arr[j]>arr[j+1]:
        arr[j],arr[j+1]=arr[j+1],arr[j]
        swapped=True
    if not swapped:
      break

  return arr

def insertion_sort(arr):
  n=len(arr)
  for i in range(1,n):
    key=arr[i]
    j=i-1
    while j>=0 and arr[j]>key:
      arr[j+1]=arr[j]
      j-=1
    arr[j+1]=key

  return arr

